import React, { useContext } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import {AuthContext} from '../../Shared/components/context/auth-context'
            // import Input from '../../Shared/components/FormElements/Input';
            // import Button from '../../Shared/components/FormElements/Button';
            import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH, VALIDATOR_MAXLENGTH, VALIDATOR_MIN } from '../util/validators';
            import useFormNewListing from './useFormNewListing';
import validateInfoNewListing from './validateInfoNewListing';
import ListingCategories from '../../Shared/components/FormElements/ListingCategories';
// import ImageUpload from '../../Shared/components/FormElements/ImageUpload';

import './NewListing.css';


const NewListing = ({ submitForm }) => {

  const auth = useContext(AuthContext);
  const history = useHistory()

  const {handleChange, values, handleSubmit, errors} = useFormNewListing (
    submitForm,
    );

    const userId = useParams().id
    console.log("userId:", userId)

  return (
    <form className="listing-form" onSubmit={handleSubmit}>
      <label>Category</label>
      <select
        classname="form-input"
        type="text"
        name="category"
        value={values.category}
        onChange={handleChange}
      >
        <option value="Paper">Paper</option>
        <option value="Plastic">Plasticr</option>
        <option value="Electronic">Electronic</option>
        <option value="Glass">Glass</option>
        <option value="Metal">Metal</option>
        <option value="Furniture">Furniture</option>
        <option value="Textile">Textile</option>
        <option value="Recyclables">Recyclables</option>
        <option value="Other">Other</option>

        {/* <ListingCategories id="plastic" displayedCategory="Plastic" />
        <ListingCategories id="electronic" displayedCategory="Electronic" />
        <ListingCategories id="glass" displayedCategory="Glass" />
        <ListingCategories id="metal" displayedCategory="Metal" />
        <ListingCategories id="furniture" displayedCategory="Furniture" />
        <ListingCategories id="textile" displayedCategory="Textile" />
        <ListingCategories id="recyclabes" displayedCategory="Recyclables" />
        <ListingCategories id="other" displayedCategory="Other" /> */}
        {/* {errors.category && <p>{errors.category}</p>} */}
      </select>
      <label>Title</label>
      <input
        classname="form-input"
        type="text"
        placeholder="Enter a title"
        onChange={handleChange}
      />
      {/* {errors.title && <p>{errors.title}</p>} */}
      <label>Description</label>
      <textarea
        classname="form-input"
        type="text"
        placeholder="Enter a description"
        onChange={handleChange}
      />
      {errors.description && <p>{errors.description}</p>}
      <label>Quantity</label>
      <input
        classname="form-input"
        type="number"
        min="1"
        placeholder="Enter a quantity"
        onChange={handleChange}
      />
      {/* {errors.quantity && <p>{errors.quantity}</p>} */}
      <label>Location</label>
      <input
        classname="form-input"
        type="text"
        placeholder="Enter a location"
        onChange={handleChange}
      />
      {/*  IMAGE UPLOAD {errors.quantity && <p>{errors.quantity}</p>}
      <label>Image</label>
      <input
        classname="form-input"
        type="file"
        placeholder="Enter an Image"
        onChange={handleChange}
      /> */}
      {/* {errors.location && <p>{errors.location}</p>} */}
      <button className="form-input-btn" type="submit">
        Add Listing
      </button>
    </form>
  ); 
} 

export default NewListing;
