import React, { useState, useContext } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import {AuthContext} from '../Shared/components/context/auth-context'
import UseFormNewListing from '../components/ListingForm/UseFormNewListing';
import ListingsByUserId from './ListingsByUserId';
import Card from '../Shared/components/UIElements/Card';

// import './NewListing.css';

const EditListing = () => {

    const auth = useContext(AuthContext);

    const [saveError, setSaveError] = useState()

    const userId = useParams();

    const userIdString = userId.id;

    const history = useHistory()

    const onSave = async (newListing) => {
      try{
        const response = await fetch('/api/listings/:lid', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
          },
            body: JSON.stringify(newListing)
        })

        console.log('Edit response is', response)
              if (response.status === 500) {
                  let errorMessage = await response.text()
                  console.log('We had an error.  it was: ', errorMessage)
                  setSaveError(errorMessage)
              }
              else {
                  setSaveError(undefined)
                  history.push(`/user_profile/${userIdString}`)

          }
        } catch (error) {
              console.error('Fetch failed to reach the server.')
          }
    }

    return (
      <Card>
      <ListingsByUserId />
      <UseFormNewListing 
          onSave={onSave} 
          saveError={saveError} 
          saveButtonCaption="Edit Listing"
      />
      </Card>
  )

}
export default EditListing;
