import React, { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

import Card from "../Shared/components/UIElements/Card";

const UserListings = () => {

    const [loadedUsers, setLoadedUsers] = useState([]);

  const userId = useParams();

  const userIdString = userId.id

//   console.log("userid:", userId.id)

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        let receivedData = await fetch(`/api/users/${userIdString}`, 
        {
          method: "GET",
            headers: {
              "Content-Type": "application/json",
            }
        });
        // console.log("receivedData:", receivedData)
        let responseData = await receivedData.json();
        console.log("responseData1:", responseData.user)
        console.log("responseData2:", responseData.user.listings)
        console.log("responseData3:", responseData.user.username)
        // let finalDataKeys = Object.keys(responseData.user);
        // let finalDataValues = Object.values(responseData.user)
        // console.log("finalData:", finalDataValues)
        setLoadedUsers(responseData.user);
        console.log("loadedUsers:", loadedUsers);
      } catch (err) {
        console.log("error:", err);
      }
    };
    fetchUsers();
  }, [userId]);

  return (
    <React.Fragment>
      <div className="main-div">
        <div>
          <h2 className="h2"> All listings from </h2>
          <div className="user-profile-wrap-div">
                <Card>
                  <div>
                    <div>Username: {responseData.user.username} </div>
                    <div>Email: {responseData.user.email} </div>
                    <div>Image: {responseData.user.image} </div>
                    <div>Listings: {responseData.user.listings} </div>
                  </div>
                </Card>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default UserListings;
