import React, { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

import Card from "../Shared/components/UIElements/Card";

const UserListings = () => {
  const [loadedListings, setLoadedListings] = useState();

  const userId = useParams();

  console.log("userid:", userId)

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const receivedData = await fetch(`/api/users/${userId}`, {
          method: "GET",
          //   headers: {
          //     "Content-Type": "application/json",
          //   }
        });
        setLoadedListings(receivedData);
        console.log("loadedListings:", loadedListings);
      } catch (err) {
        console.log("error:", err);
      }
    };
    fetchListings();
  }, [userId]);

  return (
    <React.Fragment>
      <div className="main-div">
        <div>
          <h2 className="h2"> All listings from </h2>
          <div className="user-profile-wrap-div">
            {loadedListings.map((listing) => {
              return (
                <Card>
                  <div>
                    <div>Username: {listing.username} </div>
                    <div>Email: {listing.email} </div>
                    <div>Image: {listing.image} </div>
                    <div>Listings: {listing.listings} </div>
                  </div>
                </Card>
              );
            })}
            {}
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default UserListings;
