import React, { useState, useEffect } from "react";

import { useParams } from "react-router-dom";

import Card from "../Shared/components/UIElements/Card";

const UserListings = () => {

    const [loadedUsers, setLoadedUsers] = useState([]);
    const [loadedListings, setLoadedListings] = useState([]);
    const [loading, setLoading] = useState(false);

  const userId = useParams();

  const userIdString = userId.id

//   console.log("userid:", userId.id)

  useEffect(() => {

    const fetchUsers = async () => {
      try {
        let receivedData = await fetch(`/api/users/${userIdString}`, 
        {
          method: "GET",
            headers: {
              "Content-Type": "application/json",
            }
        });
        let responseData = await receivedData.json();
        setLoadedUsers(responseData.user);
    } catch (err) {
        console.log("error:", err);
    }
    console.log("loadedUsers:", loadedUsers);
    };
    fetchUsers();
  }, [userId]);



  useEffect(() => {
      setLoading(true);
    const fetchListings = async () => {
      try {
        let receivedListings = await fetch(`/api/listings/user/${loadedUsers.id}`, 
        {
          method: "GET",
            headers: {
              "Content-Type": "application/json",
            }
        });
        let responseListings = await receivedListings.json();
        console.log("responseListings:", responseListings.listings)
        // const finalResponse = responseListings.listings
        // console.log("finalResponse:", finalResponse);
        // setLoadedListings(responseListings.listings);
        const finalData = responseListings.listings
        console.log("finalData:", finalData)
        setLoadedListings(responseListings.listings);

    } catch (err) {
        console.log("error:", err);
    }
    setLoading(false);
};
fetchListings();
}, [loadedUsers]);

console.log("loadedListings:", loadedListings);



  return (
    <React.Fragment>
      <div className="main-div">
        <div>
          <h2 className="h2"> All listings from {loadedUsers.username}</h2>
          <div className="user-profile-wrap-div">
                <Card>
                  <div>
                    <div>Username: {loadedUsers.username} </div>
                    <div>Email: {loadedUsers.email} </div>
                    <div>Image: {loadedUsers.image} </div>
                    <div>Listings: {loadedUsers.listings} </div>
                    <div>Item 1: </div>
                    {/* {finalData.forEach(i => {
                        return(
                            <div>Category: {i.category}</div>
                        )
                    })} */}
                  </div>
                </Card>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default UserListings;
